using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Picture
{
    class Program
    {
        static void Main(string[] args)
        {
            //string s = "";

            string s = "";
            //应用程序目录下的目录路径
            string directoryPath = System.Environment.CurrentDirectory.Replace(@"\bin\Debug", @"");
            //目录下的文件路径
            string filePath = Path.Combine(directoryPath, string.Format("100{0}.jpg", 2));
            //若目录不存在则创建
            if (!Directory.Exists(directoryPath))
            {
                Directory.CreateDirectory(directoryPath);
            }

            FileStream fs = new FileStream(filePath, FileMode.Create, FileAccess.Write);
            BinaryWriter bw = new BinaryWriter(fs);
            byte[] array = new byte[s.Length / 2];
            for (int i = 0; i < s.Length / 2; i++)
            {
                array[i] = Convert.ToByte(s.Substring(i * 2, 2), 16);
            }
            MemoryStream stream = new MemoryStream(array);
            StreamReader reader = new StreamReader(stream);
            Console.WriteLine(array);
            for (int i = 0; i < s.Length / 2; i++)
            {
                Console.WriteLine(array[i]);
                //Console.ReadKey();
            }
            //try
            //{
            while (reader.Peek() != -1)
            {
                string tempStr = reader.ReadLine();
                byte tempByte = Convert.ToByte(tempStr, 16);
                bw.Write(tempByte);
            }
            Console.ReadKey();
            //}
            //catch
            //{
            //LoadPicture
            //}
            fs.Close();
            bw.Close();
            reader.Close();
        }
    }
}
